target.script:
	var File = Java.type('java.io.File');
    var repoDir = project.getProperty("repo.dir");

    var fs = project.createDataType("fileset");
    fs.setDir(new File(repoDir, "plugins"));
    fs.setIncludes("net.enilink.komma.core_*.jar");

    // Get the files (array) of that fileset
    var ds = fs.getDirectoryScanner(project);
    var files = ds.getIncludedFiles();

	if (files.length > 0) {
		var filename = files[0];
		var version = filename.replace(/^.*_/, '').replace('.jar', '');

		// use Ant echo task to write the version file
		var echo = project.createTask("echo");
		echo.setFile(new File(repoDir, "version.txt"));
		echo.setMessage(version);
		echo.perform();
	}
